mapper.insert:
INSERT INTO LETCRUD (
	CRUD_ID
	, CRUD_SJ
	, CRUD_CN
	, USER_NM
	, FRST_REGIST_PNTTM
) VALUES (
	#{crudId}
   , #{crudSj}
   , #{crudCn}
   , #{userNm}
   , NOW()
)

mapper.update:
UPDATE LETCRUD SET
	CRUD_SJ = #{crudSj}
	, CRUD_CN = #{crudCn}
	, USER_NM = #{userNm}
WHERE CRUD_ID = #{crudId}
